// main buttons

@mixin transparent-button {
    @include body-text;
    white-space: nowrap;
    text-transform: uppercase;
    font-size: 12px;
    font-weight: bold;

    color: $purple-color;
    text-transform: uppercase;

    background-color: #fff;
    background-clip: padding-box;
    border: 2px solid transparent;
    border-radius: 20px;

    padding: 8px 17.5px;
    margin: auto;
    position: relative;
    z-index: 1;
    &::before {
        content: '';
        position: absolute;
        top: 0; left: 0; bottom: 0; right:0;
        margin: -2px;
        z-index: -1;

        background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
        border-radius: 20px;
    }

}

@mixin transparent-button-hover {
    border: 2px solid rgba(225, 225, 225, 0.5);
}

@mixin purple-button {
    @include body-text;
    text-transform: uppercase;
    font-size: 12px;
    font-weight: bold;

    color: #FFF;
    padding: 9.5px 20px;
    border-radius: 22px;
	background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
}

@mixin purple-button-hover {
    opacity: 0.5;
}

//small buttons

@mixin radio-check {
    position: absolute;
    z-index: -1;
    opacity: 0;
    &+label{
        display: inline-flex;
    }
    &+label::before {
        content: "";
        width: 20px;
        height: 20px;
        border: 1px solid $dark-shade-25-color;
        margin-right: 10px;
        cursor: pointer;
    }
    &:checked+label::before {   
        border-color: $purple-color;
    }
}

@mixin radio {
    &+label {
        align-items: center;
    }
    &+label::before {
        border-radius: 50%;
    }
    &:checked+label {
        position: relative;
    }
    &:checked+label::after {
        content: "";
        position: absolute;
        left: 4px;
        width: 12px;
        height: 12px;
        border-radius: 50%;
        background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
    }
}

@mixin check {
    &+label {
        line-height: 18px;
    }
    &+label::before {
        border-radius: 4px;
        background-repeat: no-repeat;
        background-position: center center;
        background-size: 50% 50%;
        flex-shrink: 0;
        flex-grow: 0;
    }
    &:checked+label::before {
        background-image: url(../src/img/arrow-check.svg);
    }
}

@mixin toggle {
    position: relative;
    display: inline-flex;
    align-items: center;
    vertical-align: top;
    width: 40px;
    height: 20px;
    background: #FFF;
    border: 1px solid $dark-shade-25-color;
    border-radius: 10px;

    cursor: pointer;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    margin-right: 10px;

    &::after {
        content: "";
        position: absolute;
        left: 3px;
        width: 12px;
        height: 12px;
        background-color: $dark-shade-25-color;
        border-radius: 50%;
        transition: 0.3s;
    }
    &:checked::after {
        transform: translateX(calc(100% + 8px));
        background-color: #fff;  
        background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
        transition: 0.3s;
    }
    &:checked {
        border-color: $purple-color;
    }
}

